// increment the connection port
// of the tcp out node with id 20ce9864.df2028
// increment is done on every input of inject
// the flowid is set in the topic of the inject 
// node

const configCopy = JSON.parse(JSON.stringify(msg.payload.configs));

const modbusClientId = 'b8dd5dc2.87841';
const dataBaseId = '608ff9c050d10945';

configCopy.forEach(function(el) {
    if (el.id === modbusClientId) {
        el.tcpHost = env.get("MODBUS_TCP_IP")
    }
    if (el.id === dataBaseId) {
        el.host = env.get("MYSQL_IP")
    }
});

if (JSON.stringify(msg.payload.configs) === JSON.stringify(configCopy)) return

msg.payload.configs = configCopy
msg.headers = {
    'Content-Type': 'application/json',
    'Accept' : 'application/json'};
msg.url = "http://127.0.0.1:1880/flow/global"
msg.method ="PUT";

return msg;
